    dateCreated: new Date("2022-12-21"),
  },
  {
    _id: "t01",
    gerne: "Thought",
    title: `Queen Elizabeth II`,
    coverImage:
      "https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/inspirational-quote-hrm-queen-elizabeth-ii-1665765893.png?crop=1xw:1xh;center,top&resize=980:*",
    content: `It is often the small steps, not the giant leaps, that bring about the most lasting change.`,
    dateCreated: new Date("2022-11-11"),
  },
  {
    _id: "t02",
    gerne: "Thought",
    title: `Nelson Mandela`,
    coverImage:
      "https://hips.hearstapps.com/hmg-prod.s3.amazonaws.com/images/inspirational-quote-nelson-mandela-1665766696.png?crop=1xw:1xh;center,top&resize=980:*",
    content: `Education is the most powerful weapon which you can use to change the world.`,
    dateCreated: new Date("2022-12-12"),
  },
];

function App() {
  const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    getAllBlogs();
  }, []);

  const getAllBlogs = async () => {
    await axios
      .get("http://localhost:5000/blogs")
      .then((response) => setBlogs(response.data));
    //console.log(tweets);
  };
  return (
    <Fragment>
      <Header />
      <Routes>
        <Route path="/home" element={<HomePage data={blogs} />} />
        <Route
          path="/blogs/:blogId"
          element={<BlogDetailPage data={blogs} />}
        />
        <Route path="/admin" element={<AdminPage />} />
      </Routes>
    </Fragment>
  );
}

export default App;
