import { useState, Fragment } from "react";
import classes from "./AdminPage.module.css";
import "react-quill/dist/quill.snow.css";
import ReactQuill from "react-quill";
import DOMPurify from "dompurify";
import CustomToolbar from "../components/helpers/ReactQuillEditor/CustomToolbar";

const AdminPage = () => {
  const [addBlog, setAddBlog] = useState(false);
  const [title, setTitle] = useState("");
  const [image, setImage] = useState("");
  const [description, setDescription] = useState("");
  const [gerne, setGerne] = useState("");
  const modules = {
    toolbar: {
      container: "#toolbar",
    },
  };
  const formats = [
    "font",
    "size",
    "bold",
    "italic",
    "underline",
    "strike",
    "color",
    "background",
    "script",
    "header",
    "blockquote",
    "code-block",
    "indent",
    "list",
    "direction",
    "align",
    "link",
    "image",
    "video",
    "formula",
  ];
  return (
    <Fragment>
      <div className="container mt-5">
        <div className={`row ${classes.cardRow}`}>
          <div className={`col-3 ${classes.adminCard}`}>
            <h1>Add a new Blog</h1>
          </div>
          <div className={`col-3 ${classes.adminCard}`}>
            <h1>Delete a Blog</h1>
          </div>
          <div className={`col-3 ${classes.adminCard}`}>
            <h1>Modify a Blog</h1>
          </div>

          <div className={`${classes.addBlogForm}`}>
            <form className="form">
              <label for="">Title</label>
              <input
                value={title}
                type="text"
                className="form-control"
                onChange={(e) => {
                  setTitle(e.target.value);
                }}
              />
              <label for="">Upload Image</label>
              <input
                value={image}
                type="file"
                multiple
                accept="image/*"
                className="form-control"
                onChange={(e) => {
                  setImage(e.target.value);
                }}
              />
              <label for="">Gerne</label>
              <select
                value={gerne}
                name="gernes"
                className="form-control"
                onChange={(e) => {
                  setGerne(e.target.value);
                }}
              >
                <option value="Blog">Blog</option>
                <option value="Poem">Poem</option>
                <option value="Thought">Thought</option>
                <option value="Story">Story</option>
              </select>
              <label for="">Content</label>
              <div className={classes.customizeQuill}>
                <CustomToolbar />
                <ReactQuill
                  formats={formats}
                  modules={modules}
                  value={description}
                  onChange={setDescription}
                  bounds={classes.customizeQuill}
                />
              </div>
              <div className={classes.submit}>
                <button>Submit</button>
              </div>
            </form>
          </div>
        </div>
        <div
          dangerouslySetInnerHTML={{ __html: DOMPurify.sanitize(value) }}
          className={classes.content}
        />
      </div>
    </Fragment>
  );
};

export default AdminPage;
