import { useState, Fragment } from "react";
import classes from "./AdminPage.module.css";
import "react-quill/dist/quill.snow.css";
import ReactQuill from "react-quill";
import DOMPurify from "dompurify";
import CustomToolbar from "../components/helpers/ReactQuillEditor/CustomToolbar";

const AdminPage = () => {
  const [value, setValue] = useState("");
  const [addBlog, setAddBlog] = useState(false);
  const modules = {
    toolbar: {
      container: "#toolbar",
    },
  };
  const formats = [
    "font",
    "size",
    "bold",
    "italic",
    "underline",
    "strike",
    "color",
    "background",
    "script",
    "header",
    "blockquote",
    "code-block",
    "indent",
    "list",
    "direction",
    "align",
    "link",
    "image",
    "video",
    "formula",
  ];
  return (
    <Fragment>
      <div className="container mt-5">
        <div className={`row ${classes.cardRow}`}>
          <div className={`col-3 ${classes.adminCard}`}>
            <h1>Add a new Blog</h1>
          </div>
          <div className={`col-3 ${classes.adminCard}`}>
            <h1>Delete a Blog</h1>
          </div>
          <div className={`col-3 ${classes.adminCard}`}>
            <h1>Modify a Blog</h1>
          </div>
          <div className={`${classes.addBlogForm}`}>
            <form>
              <label for="">Title</label>
              <input type="text" className="form-control" />
              <label for="">Gerne</label>
              <select name="gernes" className="form-control">
                <option value="Blog">Blog</option>
                <option value="Poem">Poem</option>
                <option value="Thought">Thought</option>
                <option value="Story">Story</option>
              </select>
              <label for="">Content</label>
              <div className={classes.customizeQuill}>
                <CustomToolbar />
                <ReactQuill
                  formats={formats}
                  modules={modules}
                  value={value}
                  onChange={setValue}
                  bounds={classes.customizeQuill}
                />
              </div>
              <div className={classes.submit}>
                <button>Submit</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default AdminPage;
