import { useState } from "react";
import axios from "axios";
import AdminPage from "../pages/AdminPage";
import { useSelector } from "react-redux";

const AddBlog = (props) => {
  const title = useSelector((state) => state.blog.blogTitle);
  const image = useSelector((state) => state.blog.coverImage);
  const content = useSelector((state) => state.blog.content);
  const gerne = useSelector((state) => state.blog.gerne);
  const postBlog = async () => {
    axios
      .post("http://localhost:5000/blog/add-blog", {
        title,
        image,
        content,
        gerne,
      })
      .then((res) => {
        console.log(res);
      });
  };
  return (
    <div className="">
      <p>Blog Added Successfully</p>
      <AdminPage onReadyToPostBlog={postBlog} />
    </div>
  );
};

export default AddBlog;
